CREATE TABLE "crowdfunding_platform"."campaign_report" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "reported_campaign_id" UUID NOT NULL, "reporting_user_id" UUID NOT NULL, "reason" text NOT NULL, "investigating_user_id" UUID NOT NULL, "is_resolved" boolean NOT NULL, "resolution_remark" text NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("reported_campaign_id") REFERENCES "crowdfunding_platform"."campaign"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("reporting_user_id") REFERENCES "crowdfunding_platform"."user"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("investigating_user_id") REFERENCES "crowdfunding_platform"."user"("id") ON UPDATE cascade ON DELETE set null);
CREATE EXTENSION IF NOT EXISTS pgcrypto;
